import React, { useRef } from 'react';
import AccessoriesGrid from './AccessoriesGrid';
import ClownContainer from './ClownContainer';


function App() {
  const clownContainerRef = useRef(null);
  const [clownZoneDataUrl, setClownZoneDataUrl] = useState(null);

  const handleDownload = () => {
    const clownContainer = clownContainerRef.current;

    // Get the dimensions of the clown container
    const rect = clownContainer.getBoundingClientRect();
    const { width, height } = rect;

    // Create a canvas element
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    const context = canvas.getContext('2d');

    // Draw the clown container on the canvas
    context.drawImage(clownContainer, 0, 0, width, height);

    // Get the data URL of the canvas image
    const dataUrl = canvas.toDataURL();

    // Set the data URL in the state
    setClownZoneDataUrl(dataUrl);
  };

  return (
    <main>
      <div id="header">
        <h1>WELCOME TO THE CLOWN ZONE</h1>
      </div>

      <div id="wrapper">
        <div id="clownZone" ref={clownZoneRef}>
          <ClownContainer ref={clownContainerRef}/>
        </div>

        <div id="accessories">
          <h2>Accessories</h2>
          <AccessoriesGrid />
        </div>

        <div id="downloadButton">
          <button onClick={handleDownload}>Download Clown!</button>
        </div>
      </div>

      {clownZoneDataUrl && (
        <a href={clownZoneDataUrl} download="doll_image.png" style={{ display: 'none' }} ref={(ref) => ref && ref.click()}>
          Download Link
        </a>
      )}
    </main>
  );
};

export default App;
