import React, { useState } from 'react';
import { useDrop } from 'react-dnd';

function ClownContainer() {
  const [droppedAccessories, setDroppedAccessories] = useState([]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'accessory',
    drop: (item) => {
      moveAccessory(item);
    },
    collect: monitor => ({
      isOver: monitor.isOver(),
    }),
  }));

  const moveAccessory = (item) => {
    const { className } = item;
    const existingIndex = droppedAccessories.findIndex((accessory) => accessory.className == className);
    if (existingIndex != -1) {
      setDroppedAccessories((prevAccessories) => {
        const updatedAccessories = [...prevAccessories];
        updatedAccessories.splice(existingIndex, 1, item);
        return updatedAccessories;
      });
    } else {
      setDroppedAccessories((prevAccessories) => [...prevAccessories, item]);
    }
  };

  const renderAccessories = () => {
    console.log("This is the array: " + droppedAccessories);
    return droppedAccessories.map((accessory) => (
      <img
        key={accessory.id}
        id={accessory.id}
        src={`./assets/head/${accessory.id}.png`}
        alt={accessory.alt}
        className='hair'
        style={{
          position: 'relative',
          bottom: '560px',
          maxWidth: '120px',
          maxHeight: '96px',
        }}
      />
    ));
  }

  return (
    <div id="doll-container" ref={drop}>
      <img id="doll-base" src="./assets/doll-base.png" alt="Doll Base" />
      {renderAccessories()}
    </div>
  );
}

export default ClownContainer;
