import React, { useRef } from 'react';
import AccessoriesGrid from './AccessoriesGrid';
import ClownContainer from './ClownContainer';


function App() {
  const clownZoneRef = useRef(null);

  const handleDownload = () => {
    // Get the canvas element
    const canvas = document.createElement('canvas');
    const clownZone = clownZoneRef.current;
    const rect = clownZone.getBoundingClientRect();
    canvas.width = rect.width;
    canvas.height = rect.height;
    const context = canvas.getContext('2d');

    // Render the doll container onto the canvas
    context.drawImage(clownZone, 0, 0, rect.width, rect.height);

    // Get the data URL of the canvas image
    const dataUrl = canvas.toDataURL();

    // Create a download link
    const downloadLink = document.createElement('a');
    downloadLink.href = dataUrl;
    downloadLink.download = 'doll_image.png';

    // Trigger the download
    downloadLink.click();
  };

  return (
    <main>
      <div id="header">
        <h1>WELCOME TO THE CLOWN ZONE</h1>
      </div>

      <div id="wrapper">
        <div id="clownZone" ref={clownZoneRef}>
          <ClownContainer />
          <button onClick={handleDownload}>Download Your Clown!</button>
        </div>

        <div id="accessories">
          <h2>Accessories</h2>
          <AccessoriesGrid />
        </div>
      </div>
    </main>
  );
};

export default App;
